<template>
  <div class="spotify-test">
    <Button
      label="Play next"
      @click="playNext"
    />

    <div>device: {{ device?.id }}</div>

    <div
      v-if="isPlaying"
      class="now-playing"
    >
      <h2>Now playing</h2>
      <div v-if="nowPlayingItem">{{ nowPlayingItem.name }} ({{ nowPlayingItem.id }})</div>
      <div v-if="remaining">Remaining: {{ msToTime(remaining) }}</div>
    </div>

    <div class="queue">
      <h2>Queue</h2>
      <SpotifyTrack
        v-for="item in queue"
        :id="item.id"
        :key="item.id"
      />
    </div>
  </div>
</template>

<script setup lang="ts">
import { getTestQueue } from '~/utilities/test-data';
import SpotifyTrack from '~/components/spotify-test/SpotifyTrack.vue';
import type { PlaybackState } from '@spotify/web-api-ts-sdk';
import { msToTime } from '~/utilities/formatting';

const { sdk, profile } = useSpotify();

const queue = ref(getTestQueue());

const getNextTrack = () => {
  // random from queue
  const index = Math.floor(Math.random() * queue.value.length);
  return queue.value[index];
};

const playbackState = ref<PlaybackState | null>(null);
const updatePlaybackState = async () => {
  playbackState.value = await sdk.player.getPlaybackState();
};
const interval_updatePlaybackState = ref<number | null>(null);
onMounted(() => {
  interval_updatePlaybackState.value = setInterval(updatePlaybackState, 1000);
});
onUnmounted(() => {
  if (interval_updatePlaybackState.value != null) clearInterval(interval_updatePlaybackState.value);
});
const device = computed(() => playbackState.value?.device);
const isPlaying = computed(() => playbackState.value?.is_playing === true);
const remaining = computed(() => {
  if (playbackState.value == null || !isPlaying.value) return null;
  const progress = playbackState.value.progress_ms;
  const duration = playbackState.value.item.duration_ms;
  return duration - progress;
});
const nowPlayingItem = computed(() => {
  if (playbackState.value == null || !isPlaying.value) return null;
  return playbackState.value.item;
});
watch(nowPlayingItem, () => {
  if (nowPlayingItem.value == null) return;
  removeFromQueue(nowPlayingItem.value.id);
});

const playNext = async () => {
  const nextTrack = getNextTrack();
  await sdk.player.startResumePlayback(
    device.value.id,
    undefined,
    [`spotify:track:${nextTrack.id}`],
    undefined,
    0,
  );
  removeFromQueue(nextTrack.id);
  await updatePlaybackState();
};

const removeFromQueue = (id: string) => {
  queue.value = queue.value.filter((i) => i.id !== id);
};

const beep = () => {
  const snd = new Audio(
    'data:audio/wav;base64,',
  );
  snd.play();
};

watch(
  () => ({
    isPlaying,
    playbackState: playbackState.value,
  }),
  () => {
    console.log(isPlaying.value, playbackState.value?.progress_ms);
    if (isPlaying.value === false && playbackState.value?.progress_ms === 0) {
      beep();
    }
  },
);
</script>

<style scoped lang="scss"></style>
